def get_stream_packetmarker(s, startindex, headerlength):
    idx = headerlength
    header = []
    for i in range(headerlength):
        header.append(s[i + startindex])
    while (len(set(header)) < headerlength) and (idx < len(s)-1):
        del(header[0])
        header.append(s[idx])
        idx += 1
    return idx

def get_stream_startindex(s):
    packetstart = 0
    # packetstart = get_stream_packetmarker(s, 0, 4)
    messagestart = get_stream_packetmarker(s, packetstart, 14)
    return messagestart

